mapper.select:
SELECT FACHBEREICH_ID, FACHBEREICH_NAME, BESCHREIBUNG, DEKAN, PRODEKAN
FROM FACHBEREICH
WHERE FACHBEREICH_ID= #{fbId}

mapper.update:
	
		UPDATE FACHBEREICH 
        SET FACHBEREICH_ID = #{fbId}, FACHBEREICH_NAME = #{name}, BESCHREIBUNG = #{beschreibung}, 
		DEKAN = #{dekan}, PRODEKAN = #{prodekan}
